import React, { useState } from "react";
import axios from "axios";

const MainView = () => {
  const [query, setQuery] = useState("");
  const [foodItems, setFoodItems] = useState([]);
  const [expandedItem, setExpandedItem] = useState(null); // State to track which item is expanded

  const handleSearch = async (e) => {
    e.preventDefault();
    if (query) {
      try {
        const response = await axios.get(
          `http://localhost:8000/api/search/?query=${query}`
        );
        setFoodItems(response.data.foods);
      } catch (error) {
        console.error("Error fetching food data:", error);
      }
    }
  };

  const toggleExpand = (index) => {
    setExpandedItem(expandedItem === index ? null : index); // Toggle the expanded item
  };

  return (
    <div>
      <h1>Nutrition Tracker</h1>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for food..."
          required
        />
        <button type="submit">Search</button>
      </form>
      <ul>
        {foodItems.map((item, index) => (
          <li key={index}>
            <div onClick={() => toggleExpand(index)}>
              <strong>{item.description}</strong>{" "}
              {item.brandName && `(${item.brandName})`}
            </div>
            {expandedItem === index && (
              <div>
                <p>
                  <strong>Ingredients:</strong> {item.ingredients.join(", ")}
                </p>
                <p>
                  <strong>Data Type:</strong> {item.dataType}
                </p>
                <p>
                  <strong>Food Category:</strong> {item.foodCategory}
                </p>
                <p>
                  <strong>Package Weight:</strong> {item.packageWeight}
                </p>
                <p>
                  <strong>Serving Size:</strong> {item.servingSize}{" "}
                  {item.servingSizeUnit}
                </p>
                <p>
                  <strong>Nutrients:</strong>
                </p>
                <ul>
                  {item.foodNutrients.map((nutrient, nutrientIndex) => (
                    <li key={nutrientIndex}>
                      {nutrient.nutrientName}: {nutrient.value}{" "}
                      {nutrient.unitName}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MainView;
