import React, { useState } from "react";
import axios from "axios";

const MainView = () => {
  const [query, setQuery] = useState("");
  const [foodItems, setFoodItems] = useState([]);
  const [expandedIndex, setExpandedIndex] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (query) {
      try {
        const response = await axios.get(
          `http://localhost:8000/api/search/?query=${query}`
        );
        setFoodItems(response.data.foods);
        setExpandedIndex(null); // Reset expanded index on new search
      } catch (error) {
        console.error("Error fetching food data:", error);
      }
    }
  };

  const toggleExpand = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div>
      <h1>Nutrition Tracker</h1>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for food..."
          required
        />
        <button type="submit">Search</button>
      </form>
      <ul>
        {foodItems.map((item, index) => (
          <li key={index}>
            <div
              onClick={() => toggleExpand(index)}
              style={{ cursor: "pointer" }}
            >
              {item.description}
              {/* Show basic info or indicator for expand/collapse */}
              {expandedIndex === index ? " ▲" : " ▼"}
            </div>
            {expandedIndex === index && (
              <div>
                <p>
                  <strong>Data Type:</strong> {item.dataType}
                </p>
                <p>
                  <strong>Brand Name:</strong> {item.brandName}
                </p>
                <p>
                  <strong>Ingredients:</strong> {item.ingredients.join(", ")}
                </p>
                <p>
                  <strong>Food Category:</strong> {item.foodCategory}
                </p>
                <p>
                  <strong>Package Weight:</strong> {item.packageWeight}
                </p>
                <p>
                  <strong>Serving Size Unit:</strong> {item.servingSizeUnit}
                </p>
                <p>
                  <strong>Serving Size:</strong> {item.servingSize}
                </p>
                <h4>Nutrients:</h4>
                <ul>
                  {item.foodNutrients.map((nutrient, nutrientIndex) => (
                    <li key={nutrientIndex}>
                      {nutrient.nutrientName}: {nutrient.value}{" "}
                      {nutrient.unitName}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MainView;
